import React from 'react';
import styles from "./users.module.css";
import Avatar from "../../Pictures/Avatar.jpeg";

let Users = (props) => {


  //to round up a number
  let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
  // console.log("Total #", pagesCount);
  let pages = [];
  //limiting pages manually to 50
  for (let i = 1; i <= 50; i++) {
    pages.push(i);
  }

  //other option
  // for (let i=Math.max(this.props.currentPage - 5, 1); i <= Math.max(1, Math.min(this.props.currentPage + 5, pagesCount)); i++) {
  //   pages.push(i);
  // }


  return <div className={styles.usersContainer}>

    <div className={styles.pagesScroll}>
      {pages.map(p => {
        // console.log("Alex",props.onPageChange)
        return <span className={props.currentPage === p && styles.selectedPage}
                     onClick={(e) => {
                       props.onPageChange(p)
                     }}>{p}</span>
      })}
    </div>
    {

      props.users.map(u =>
              <div key={u.id}>
      <span>
        <div>
          <img src={u.photos.small != null ? u.photos.small : Avatar}
               className={styles.userPhoto}/>
        </div>
        <div>
          {u.followed
              ? <button onClick={() => {
                props.unfollow(u.id)
              }}>Unfollow</button>
              : <button onClick={() => {
                props.follow(u.id)
              }}>Follow</button>
          }
        </div>
      </span>
                <span>
          <span>
            <div>{u.name}</div>
            <div>{u.status}</div>
          </span>
          <span>
            {/*for future use*/}
            <div>{"u?.location?.country"}</div>
            <div>{"u?.location?.city"}</div>
          </span>
        </span>
              </div>
      )
    }


  </div>
}

export default Users;
